svg.script:
document.querySelector('svg').addEventListener('show', function() {
	show(false);
});

document.querySelector('svg').addEventListener('hide', function() {
	show(true);
});

function show(hide) {
	[
		{ selector: '.st01', transitionShow: 'stroke-dashoffset .8s linear', transitionHide: 'stroke-dashoffset .8s linear' },
		{ selector: '.st02', transitionShow: 'stroke-dashoffset .48s linear .32s', transitionHide: 'stroke-dashoffset .48s linear' },
		{ selector: '.st03', transitionShow: 'stroke-dashoffset .28s linear .52s', transitionHide: 'stroke-dashoffset .28s linear' }
	].map(function(anim) {
		var path = document.querySelector(anim.selector);
		var length = path.getTotalLength();

		path.style.strokeDasharray = length + ' ' + length;

		if (hide) {
			path.style.transition = path.style.WebkitTransition = anim.transitionHide;
			path.style.strokeDashoffset = length;
			return;
		}

		path.style.transition = path.style.WebkitTransition = 'none';
		path.style.strokeDashoffset = length;
		path.getBoundingClientRect();
		path.style.transition = path.style.WebkitTransition = anim.transitionShow;
		path.style.strokeDashoffset = "0";
	});
}
